import Vue from 'vue'
import VueRouter from 'vue-router'
import BookManager from '../views/BookManager'
import Index from '../views/Index'
Vue.use(VueRouter)

const routes = [
  {
    path:"/",
    name:"图书管理",
    component:Index,
    redirect:"/BookManager",
    children:[
      {
        path:"/BookManager",
        name:"查询图书",
        component:BookManager
      },
      {
        path:"/AddBook",
        name:"添加图书",
        component:AddBook
      }
    ]
  },
  {
    path:"navigation",
    name:"导航2",
    component: Index,
    children: [
      {
        path:"/pageThree",
        name:"页面3",
        component:PageThree
      },
      {
        path:"/pageFour",
        name:"页面4",
        component:PageFour
      }
    ]
  }
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

export default router
